!*DECK U10
!*CALL PROCESS
SUBROUTINE MESAGE(KMESS)
  !        ------------------------
  !
  !  PRINT A MESSAGE ON OUPUT CHANNEL UNIT 6 (JUMP A BLANK LINE FIRST)
  !
  USE globals
  IMPLICIT NONE
  CHARACTER*(*) KMESS
  !
  WRITE (6,9900) KMESS
  !
  RETURN
9900 FORMAT(/,1X,A)
END SUBROUTINE MESAGE
