   function human_size_of(data_size, human_readable) &
    result(out_string)

     implicit none

     integer(KIND=8), intent(in) :: data_size
     logical :: human_readable
     character(len = 19) :: out_string

     real(euitm_r8) :: human_size
     character(len = 2) :: size_unit

     size_unit = 'B'
     if (human_readable) then
       human_size = dble(data_size)
       if (human_size >= 1024) then
         human_size = human_size / dble(1024)
         size_unit = 'kB'
         if (human_size >= 1024) then
           human_size = human_size / dble(1024)
           size_unit ='MB'
           if (human_size >= 1024) then
             human_size = human_size / dble(1024)
             size_unit = 'GB'
           end if
         end if
       end if
       write(out_string, '(f16.3)') human_size
     else
       write(out_string, '(i16)') data_size
     end if

     out_string = trim(adjustl(out_string)) // ' ' // trim(adjustl(size_unit))

   end function human_size_of

